import React,{useEffect,useState} from 'react'
import Newsitems from './Newsitems'
import Spinner from './Spinner';
import PropTypes from 'prop-types'
import InfiniteScroll from "react-infinite-scroll-component";

const News=(props)=> {

    const [articles,setarticles]=useState([]);
    const [loading,setloading]=useState(true);
    const [page,setpage]=useState(1);
    const [totalresults,settotalresults]=useState(0);
    
    // document.title=`${props.category} - NewsMonkey`;

    const updatenews = async () => {
        props.setprogress(10);
        const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=f59364c6a0f7455cbfd21c5525a058d0&page=${this.state.page}&pageSize=${props.pagesize}`;
        let data = await fetch(url);
        props.setprogress(30);
        let parseddata = await data.json();
        props.setprogress(70);
        setarticles(parseddata.articles);
        settotalresults(parseddata.totalresults);
        setloading(false);
        props.setprogress(100);
    }

    useEffect(()=>{
        this.updatenews();
    },[])


    fetchMoreData = async () => {
        this.setState({page:this.state.page+1})

        const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=f59364c6a0f7455cbfd21c5525a058d0&page=${this.state.page}&pageSize=${props.pagesize}`;

        this.setState({ loading: true });
        let data = await fetch(url);
        let parseddata = await data.json();
        this.setState({ loading: false });
        this.setState({
            articles: this.state.articles.concat(parseddata.articles),
            totalresults: parseddata.totalResults,
            loading: false
        })
      };

        return (
            <>
                <h1 className='text-center'>{props.title} from {props.category}</h1>
                {this.state.loading && <Spinner />}
                <InfiniteScroll
                    dataLength={this.state.articles.length}
                    next={this.fetchMoreData}
                    hasMore={this.state.articles.length !== this.state.totalresults}
                    loader={<Spinner/>}
                    >
                    <div className='container'>
                    <div className='row my-3'>
                        {this.state.articles.map((element) => {
                            return <div className='col-md-4' key={element.url}>
                                <Newsitems title={element.title ? element.title.slice(0, 50) : "No-Title"} description={element.description ? element.description.slice(0, 100) : "No-Description"} imageurl={element.urlToImage ? element.urlToImage : "https://images.squarespace-cdn.com/content/v1/5d2ce5f963185f0001cf1271/944adaeb-b231-4465-be12-3922f063c99b/new_monkey_4.jpg?format=750w"} newsurl={element.url} author={element.author} date={element.publishedAt} source={element.source.name} />
                            </div>
                        })}
                    </div>
                    </div>
                </InfiniteScroll>
                
                {/* <div className='container d-flex justify-content-between'>
                    <button disabled={this.state.page <= 1} type="button" class="btn btn-dark" onClick={this.handleprevious}>&larr; Previous</button>
                    <button disabled={this.state.page + 1 > Math.ceil(this.state.totalresults / props.pagesize)} type="button" class="btn btn-dark" onClick={this.handlenext}>Next &rarr;</button>
                </div> */}
            </>
        )
}

News.defaultProps = {
    country: 'in',
    pagesize: 20,
    category: 'general'
}

News.propTypes = {
    country: PropTypes.string,
    pagesize: PropTypes.number,
    category: PropTypes.string
}

export default News;
