import React, { Component } from 'react'
import Newsitems from './Newsitems'
import Spinner from './Spinner';
import PropTypes from 'prop-types'
import InfiniteScroll from "react-infinite-scroll-component";


export default class News extends Component {

    articles = [
        {
            "source": {
                "id": "the-verge",
                "name": "The Verge"
            },
            "author": "Mia Sato",
            "title": "How one small company’s SEO garbage made it to Sports Illustrated and USA Today",
            "description": "A company called AdVon Commerce was behind seemingly AI-generated content at Sports Illustrated and USA Today. Its cofounder, Ben Faw, has flooded the web with garbage for years.",
            "url": "http://www.theverge.com/24195879/advon-commerce-ai-sports-illustrated-gannett-product-reviews-spam-seo",
            "urlToImage": "https://cdn.vox-cdn.com/thumbor/zdSCFyROVfX4GHPp-KxnOGdT3YQ=/0x0:2000x1333/1200x628/filters:focal(1000x667:1001x668)/cdn.vox-cdn.com/uploads/chorus_asset/file/25523941/Hanneke_Rozemuller_Advon_lede.jpg",
            "publishedAt": "2024-07-11T14:00:00Z",
            "content": "The man behind the AI gaffes at Sports Illustrated and USA Today has a yearslong history of filling the internet with garbage.\r\nByMia Sato, platforms and communities reporter with five years of exper… [+25064 chars]"
        },
