    constructor(){
        super();
        this.state={
            articles:[],
            loading:false,
            page:1
        }
    }

    async componentDidMount(){
        let url = "https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=f59364c6a0f7455cbfd21c5525a058d0&page-1";
        let data = await fetch(url);
        let parseddata=await data.json();
        this.setState({articles:parseddata.articles})
    }

    handlenext=()=>{

    }
    handleprevious=()=>{
        
    }

  render() {
    return (
      <div className='container my-3'>
        <h2>NewMonkey - Top Headlines</h2>
            <div className='row my-3'>
                {this.state.articles.map((element)=>{
                    return <div className='col-md-4' key={element.url}>
                    <Newsitems title={element.title? element.title.slice(0,50) : "No-Title"} description={element.description? element.description.slice(0,100) : "No-Description"} imageurl={element.urlToImage? element.urlToImage : "https://images.squarespace-cdn.com/content/v1/5d2ce5f963185f0001cf1271/944adaeb-b231-4465-be12-3922f063c99b/new_monkey_4.jpg?format=750w"} newsurl={element.url}/>
                    </div>  
                })} 
            </div>
            <div className='container d-flex justify-content-between'>
                <button type="button" class="btn btn-dark" onClick={this.handleprevious}>&larr; Previous</button>
                <button type="button" class="btn btn-dark" onClick={this.handlenext}>Next &rarr;</button>
            </div>
      </div>
    )
  }
}
