import React, { Component } from 'react';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from './components/Navbar';
import News from './components/News';

export default class App extends Component {
  render() {
    return (
      <>
        <Router>
          <LoadingBar
            color='#f11946'
            progress={progress}
            onLoaderFinished={() => setProgress(0)}
          />
          <Navbar />
          <div className='container'>
            <Routes>
              <Route path="/" element={<News key="general" pagesize={10} title="NewsMonkey - Top Headlines" country="us" category="general" />} />
              <Route path="/business" element={<News key="business" pagesize={10} title="NewsMonkey - Top Headlines" country="us" category="business" />} />
              <Route path="/sports" element={<News key="sports" pagesize={10} title="NewsMonkey - Top Headlines" country="us" category="sports" />} />
              <Route path="/entertainment" element={<News key="entertainment" pagesize={10} title="NewsMonkey - Top Headlines" country="us" category="entertainment" />} />
              <Route path="/general" element={<News key="general" pagesize={10} title="NewsMonkey - Top Headlines" country="us" category="general" />} />
              <Route path="/health" element={<News key="health" pagesize={10} title="NewsMonkey - Top Headlines" country="us" category="health" />} />
              <Route path="/science" element={<News key="science" pagesize={10} title="NewsMonkey - Top Headlines" country="us" category="science" />} />
              <Route path="/technology" element={<News key="technology" pagesize={10} title="NewsMonkey - Top Headlines" country="us" category="technology" />} />
            </Routes>
          </div>
        </Router>
      </>
    );
  }
}
